import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css';

function Dashboard() {
  const [fullName, setFullName] = useState('');

  useEffect(() => {
    // Assume username is stored in localStorage after login
    const username = localStorage.getItem('username');
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/user?username=${username}`);
        setFullName(response.data.full_name);
      } catch (error) {
        console.error('Failed to fetch user data:', error);
      }
    };
    fetchUserData();
  }, []);

  return (
    <div className="dashboard-container">
      <nav className="sidebar">
        <div className="sidebar-user">
        </div>
        <ul>
          <li><Link to="/dashboard"><i className="fas fa-tachometer-alt"></i> Dashboard</Link></li>
          <li><Link to="/product-management"><i className="fas fa-box"></i> Product</Link></li>
          <li><Link to="/transaction-management"><i className="fas fa-exchange-alt"></i> Transaction</Link></li>
          <li><Link to="/user-management"><i className="fas fa-users"></i> User</Link></li>
          <li><Link to="/logout"><i className="fas fa-sign-out-alt"></i> Log Out</Link></li>
        </ul>
      </nav>
      <main className="main-content">
        <h1>Welcome, {fullName}</h1>
        <div className="stats">
          <div className="stat-card">
            <h2>Total Sales</h2>
            <p>$30,412</p>
            <p>↑ 1.5% vs last Month</p>
          </div>
          <div className="stat-card">
            <h2>Total Order</h2>
            <p>$12,980</p>
            <p>↓ 0.7% vs last Month</p>
          </div>
        </div>
      </main>
    </div>
  );
}

export default Dashboard;
