import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './ProductManagement.css';

function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: '',
    id: '',
    category: '',
    price: '',
    stock: '',
    image_url: ''
  });

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:8000/products');
        setProducts(response.data.products);
      } catch (error) {
        console.error('Failed to fetch products:', error);
      }
    };
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({
      ...newProduct,
      [name]: value
    });
  };

  const handleAddProduct = async () => {
    try {
      const response = await axios.post('http://localhost:8000/products', newProduct);
      setProducts([...products, response.data.product]);
    } catch (error) {
      console.error('Failed to add product:', error);
    }
  };

  return (
    <div className="dashboard-container">
      <nav className="sidebar">
        <div className="sidebar-user">
        </div>
        <ul>
          <li><Link to="/dashboard"><i className="fas fa-tachometer-alt"></i> Dashboard</Link></li>
          <li className="active"><Link to="/product-management"><i className="fas fa-box"></i> Product Management</Link></li>
          <li><Link to="/transaction-management"><i className="fas fa-exchange-alt"></i> Transaction</Link></li>
          <li><Link to="/user-management"><i className="fas fa-users"></i> User Management</Link></li>
          <li><Link to="/logout"><i className="fas fa-sign-out-alt"></i> Log Out</Link></li>
        </ul>
      </nav>
      <main className="main-content">
        <div className="product-management">
          <h1>Products</h1>
          <table>
            <thead>
              <tr>
                <th>Product</th>
                <th>Id</th>
                <th>Category</th>
                <th>Stock</th>
                <th>Price</th>
                <th>Image</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {products.map(product => (
                <tr key={product.id}>
                  <td>{product.name}</td>
                  <td>{product.id}</td>
                  <td>{product.category}</td>
                  <td>{product.stock}</td>
                  <td>${product.price}</td>
                  <td><img src={product.image_url} alt={product.name} width="50" /></td>
                  <td>
                    <button>Edit</button>
                    <button>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <h2>Add New Product</h2>
          <div className="add-product-form">
            <input type="text" name="name" placeholder="Product Name" value={newProduct.name} onChange={handleInputChange} />
            <input type="text" name="id" placeholder="Product ID" value={newProduct.id} onChange={handleInputChange} />
            <input type="text" name="category" placeholder="Category" value={newProduct.category} onChange={handleInputChange} />
            <input type="text" name="price" placeholder="Price" value={newProduct.price} onChange={handleInputChange} />
            <input type="text" name="stock" placeholder="Stock" value={newProduct.stock} onChange={handleInputChange} />
            <input type="text" name="image_url" placeholder="Image URL" value={newProduct.image_url} onChange={handleInputChange} />
            <button onClick={handleAddProduct}>Add Product</button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default ProductManagement;
