import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css';

function Dashboard() {
  const [fullName, setFullName] = useState('');

  useEffect(() => {
    // Assume username is stored in localStorage after login
    const username = localStorage.getItem('username');
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/user?username=${username}`);
        setFullName(response.data.full_name);
      } catch (error) {
        console.error('Failed to fetch user data:', error);
      }
    };
    fetchUserData();
  }, []);

  return (
    <div className="dashboard-container">
      <nav className="sidebar">
        <div className="sidebar-user">
          <p>{fullName}</p>
        </div>
        <ul>
          <li><Link to="/dashboard"><img src="/icon/dashboard.png" alt="Dashboard" /> Dashboard</Link></li>
          <li><Link to="/product-management"><img src="/icons/product.png" alt="Product Management" /> Product Management</Link></li>
          <li><Link to="/transaction-management"><img src="/icons/transaction.png" alt="Transaction" /> Transaction</Link></li>
          <li><Link to="/reports"><img src="/icons/reports.png" alt="Reports" /> Reports</Link></li>
          <li><Link to="/user-management"><img src="/icons/users.png" alt="User Management" /> User Management</Link></li>
          <li><Link to="/logout"><img src="/icons/logout.png" alt="Log Out" /> Log Out</Link></li>
        </ul>
      </nav>
      <main className="main-content">
        <h1>Welcome, {fullName}</h1>
        <div className="stats">
          <div className="stat-card">
            <h2>Total Sales</h2>
            <p>$30,412</p>
            <p>↑ 1.5% vs last Month</p>
          </div>
          <div className="stat-card">
            <h2>Total Order</h2>
            <p>$12,980</p>
            <p>↓ 0.7% vs last Month</p>
          </div>
        </div>
      </main>
    </div>
  );
}

export default Dashboard;
